    def write_number(self, number, check):
        if check == 0:
            self.s = True
        if self.s:
            if self.label.text() == "0":
                self.label.setText(" "  + number)
            else:
                self.label.setText(self.label.text() + number)
            if check == 1:
                self.s = False

    def del_number(self):
        lenth = len(self.label.text())
        text = self.label.text()
        chek = text[lenth - 1: lenth]
        if lenth > 3 and chek in " ":
            text = text[:lenth - 3]
            
        else:
            if lenth <= 1:
                pass
            else:
                text = text[:lenth - 1]
        self.label.setText(text)

    def delete(self):
        self.label.setText(" ")
        if self.attempt:
            self.t1 = time.perf_counter()
            self.attempt = not self.attempt
        else:
            t2 = time.perf_counter()
            if t2 - self.t1 < 1.5:
                self.textBrowser.clear()
            self.attempt = not self.attempt


    def results(self):
        # if self.label.text() 
        res = eval(self.label.text())
        self.label.setText(self.label.text() + " = " + str(res))
        self.textBrowser.append(self.label.text())
        self.label.setText(" " + str(res))
        self.s = 0

